angular.module("ngLiveHighlights", ["datatables", "angularMoment", "ngSanitize", "ngRoute", "ngAnimate"]).config(function($routeProvider, $locationProvider) {
    $routeProvider.when("/", {
        templateUrl: "pages/HomePage.html",
        controller: "LiveFootballScontroller",
        animation: "ShowView"
    }).when("/Goals", {
        templateUrl: "pages/HighlightsAndGoals.html",
        controller: "HighlightsAndGoals",
        animation: "ShowView"
    }).when('/Guide', {
        templateUrl: 'pages/Guide.html',
        animation: 'ShowView'
    }).when('/sopcast-guide', {
        templateUrl: 'pages/Guide.html',
        animation: 'ShowView'
    }).when('/acestream-guide', {
        templateUrl: 'pages/Guide.html',
        animation: 'ShowView'
    }).when("/About", {
        templateUrl: "pages/About.html",
        controller: "HighlightsAndGoals",
        animation: "ShowView"
    }).otherwise({
        redirectTo: "/",
        animation: "ShowView"
    });


        // use the HTML5 History API
        // $locationProvider.html5Mode(true);

}), $(document).ready(function() {
    $(".button-collapse").sideNav(), $("ul.tabs").tabs()
});